mapper.select:
select * from fac_sta
where REPO_NUM = #{REPO_NUM}
and TYPE = #{TYPE}
and CAT = #{CAT}
and BRAND = #{BRAND}
and NAME = #{NAME}
and OWNER = #{OWNER}

      
		select count(*) from fac_sta
		where TYPE = #{TYPE}
		and REPO_NUM like CONCAT('%', #{REPO_NUM}, '%')

      
		select * from fac_sta
		where TYPE = #{TYPE}
		and REPO_NUM like CONCAT('%', #{REPO_NUM}, '%')
		order by ID
		limit #{START_PAGE}, #{EACH_PAGE}

      
		select * from fac_sta
		where TYPE = #{TYPE}
		and REPO_NUM like CONCAT('%', #{REPO_NUM}, '%')
